import React, {useEffect, useState} from 'react';

export default function Header() {
    const [scrollY, setScrollY] = useState('')

    useEffect(()=>{ 
        window.addEventListener('scroll', handleResize)
        return ()=>{
            window.removeEventListener('scroll', handleResize)
        }
    },[])

    //function to add class to header to change their behaviour
    function handleResize(){                
        if(window.scrollY > 0){
            setScrollY('sticky')
        }else{
            setScrollY('')
        }
        if(window.scrollY > 100){
            setScrollY('sticky small')
        }
    }

  return (
    <header className={`shrink-sticky-header ${scrollY}`}>
        <div className='wrapper'>
            <div>
                <img 
                    className='logo-img' 
                    src='https://colorlib.com/wp/wp-content/uploads/sites/2/colorlib-logo-top.png' 
                    alt='Colorlib' 
                />
            </div>
            <div>
                <ul>
                    <li>Home</li>
                    <li>Contact Us</li>
                    <li>Free Themes</li>
                    <li>Premium Themes</li>
                    <li>Blog</li>
                    <li> <i className="fa fa-shopping-cart"></i></li>
                    <li><i className="fa fa-search"></i></li>
                </ul>
            </div>
        </div>
    </header>
  );
}
