import React, { useEffect, useState } from "react";
import "../src/index.scss";
import useResize from "../src/components/hoc/resizer";
import { Provider } from "react-redux";
import store from "../src/redux/store";
import fetch from "isomorphic-unfetch";
import { useRouter } from 'next/router'
import PrivateRoute from "../src/components/PrivateRoute";

// require('https').globalAgent.options.ca = require('ssl-root-cas/latest').create();

export default function CustomApp(props) {
  const [isLoaded, setLoaded] = useState(false)
  const scrollY = useResize();
  const router = useRouter()

  function handle() {
    window.scrollTo(0, 0);
  }

  useEffect(()=>{
    setLoaded(true)
  },[])

  // const router = useRouter()
    // console.log(router);
    // router.push('/')

  // useEffect(()=>{
  //   const categories = store.getState().categories.categoryData
  //   if(props.router.route === '/contactus' && !categories.isSuccess ){
  //     router.push('/')
  //   }
  // },[])
  // console.log(store.getState());


  const { Component, ...restProps } = props;
  return (
    <>
      <div
        onClick={handle}
        className={`scroll-to-top ${scrollY > 5 ? "visible" : ""}`}
      >
        <i className="fa fa-chevron-up" />
      </div>
      {isLoaded &&
      <Provider store={store}>
        <Component {...restProps} />
      </Provider>}
    </>
  );
}
