import React from "react";
import "../src/index.scss";
import useResize from "../src/components/hoc/resizer";
import { Provider } from "react-redux";
import store from "../src/redux/store";

export default function CustomApp(props) {
  const scrollY = useResize();

  function handle() {
    window.scrollTo(0, 0);
  }

  const { Component, ...restProps } = props;  
  return (
    <>
      <div
        onClick={handle}
        className={`scroll-to-top ${scrollY > 5 ? "visible" : ""}`}
      >
        <i className="fa fa-chevron-up" />
      </div>
      <Provider store={store}>
        <Component {...restProps} />
      </Provider>
    </>
  );
}

CustomApp.getInitialProps = async ctx => {
    const res = await fetch('https://api.gyftr.net/smartbuyapi/hdfc/api/v1/home/categories')
    const categories = await res.json()
    return { categories}
  }
